import React from 'react'
import { UilSearch, UilLocationPoint} from '@iconscout/react-unicons'

function Inputs({setUnits, setQuery, units}) {
  const {city, setCity} = React.useState('')
  const handleSearchClick = () => {
    if (city !== '') setQuery(city)
  }
  return (
    <div className="flex flex-row justify-center my-6">
        <div className="flex flex-row w-3/4 items-center justify-center space-x-4">
            <input 
                value ={city}
                onChange= {(e) => setCity(e.currentTarget.value)}
                type="text" 
                className="text-xl font-light p-2 w-full shadow-xl focus:outline-none capitalize placeholder:lowercase" 
                placeholder="Search..."

            />
            <UilSearch size={25} onClick = {()=>setQuery(city)}className="text-white cursor-pointer transition ease-out hover:scale-125"/>
            <UilLocationPoint size={25} className="text-white cursor-pointer transition ease-out hover:scale-125"/>
        </div>
            <div className="flex flex-row w-1/4 items-center justify-center">
                <button name="metric" className= "text-xl text-white font-light transition ease-out hover:scale-125" onClick={(units)=>setUnits('metric')} >°C</button>
                <p className="text-xl text-white mx-1">|</p>
                <button name="imperial" className= "text-xl text-white font-light transition ease-out hover:scale-125" onClick={(units)=>setUnits('imperial')}>°F</button>
            </div>
    </div>
  )
}

export default Inputs
