import './App.css';
import UilReact from '@iconscout/react-unicons/icons/uil-react'
import TopButtons from './components/TopButtons';
import Inputs from './components/Inputs';
import TimeAndLocation from './components/TimeAndLocation';
import TemperatureAndDetails from './components/TemperatureAndDetails';
import Forecast from './components/Forecast';
import getFormattedWeatherData from './services/weatherServices';
import { useState, useEffect } from 'react';


function App() {

  const [query, setQuery] = useState({q: 'berlin'})
  const [units, setUnits] = useState('metric')
  const [weather, setWeather] = useState(null)

  useEffect(() => {
    const fetchWeather = async () => {
      const data = await getFormattedWeatherData({...query, units}).then(data => {
        setWeather(data)
      });
    }
    fetchWeather()  
  }, [query, units])

  const formatBackground = () => {
    if(!weather) return 'from-cyan-700 to-blue-700 h-fit shadow-xl'
    const threshold = units === 'metric' ? 20:60
    if (weather.temp <= threshold) return 'from-cyan-700 to-blue-700'

    return 'from-yell0'
  }


  return (
    <div
      className="mx-auto max-w-screen-md mt-4 py-5 px-32 bg-gradient-to-br from-cyan-700 to-blue-700 h-fit shadow-xl shadow-gray-400"
    >
      <TopButtons setQuery={setQuery}/>
      <Inputs setQuery={setQuery} units={units} setUnits={setUnits}/>
      {weather && (
        <div>
          <TimeAndLocation weather={weather}/>
          <TemperatureAndDetails weather={weather}/>
          <Forecast title="hourly forecast" item={weather.hourly}/>
          <Forecast title="daily forecast" item={weather.daily}/>
        </div>
      )}


    </div>
  );
}

export default App;
